import React, { useState, useEffect } from 'react';
import { ChevronUp } from 'lucide-react';
import { Link } from 'react-router-dom';

const Footer: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);

  const toggleVisibility = () => {
    if (window.pageYOffset > 300) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', toggleVisibility);
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  return (
    <footer 
      className="bg-[#1a1a1a] text-white py-8 relative" 
      style={{
        background: '#000000',
        backgroundImage: 'linear-gradient(to right, #434343, #000000)'
      }}
    >
      {isVisible && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-4 right-4 text-white p-3 rounded-full transition-all duration-300 z-50"
          aria-label="Scroll to top"
        >
          <ChevronUp size={24} className="text-white" />
        </button>
      )}
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {/* Logo Section */}
          <div>
            <Link to="/" className="flex items-center">
              <img
                src="/images/logos/New-Dreamin-Logo-White.png"
                alt="DreamIn Logo"
                className="h-8"
              />
            </Link>
          </div>

          {/* Company Links */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Company Links</h3>
            <ul className="space-y-2">
              <li><Link to="/" className="hover:text-gray-300">Home</Link></li>
              <li><Link to="/events" className="hover:text-gray-300">Events</Link></li>
              <li><Link to="/about" className="hover:text-gray-300">About</Link></li>
              <li><Link to="/careers" className="hover:text-gray-300">Careers</Link></li>
            </ul>
          </div>

          {/* Contact Section */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Contact Us</h3>
            <a href="mailto:info@dreamin.com" className="block mb-2 hover:text-gray-300">
              info@dreamin.com
            </a>
            <button className="bg-red-600 text-white px-6 py-2 rounded-md hover:bg-red-700 transition-colors">
              Contact Us
            </button>
          </div>
        </div>

        {/* Bottom Copyright */}
        <div className="mt-8 pt-4 border-t border-gray-700 text-center text-sm">
          <p>© DreamIn {new Date().getFullYear()}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
