import React, { useState, useEffect } from 'react';
import { ChevronUp } from 'lucide-react';
import { Link } from 'react-router-dom';
import { 
  IconBrandFacebook, 
  IconBrandTwitter, 
  IconBrandLinkedin, 
  IconBrandInstagram, 
  IconBrandTumblr, 
  IconBrandReddit 
} from '@tabler/icons-react';

const Footer: React.FC = () => {
  const [isVisible, setIsVisible] = useState(false);

  const toggleVisibility = () => {
    if (window.pageYOffset > 300) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  useEffect(() => {
    window.addEventListener('scroll', toggleVisibility);
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  return (
    <footer 
      className="bg-[#1E1E1E] text-white py-8 px-4" 
      style={{
        background: '#000000',
        backgroundImage: 'linear-gradient(to right, #434343, #000000)'
      }}
    >
      {isVisible && (
        <button
          onClick={scrollToTop}
          className="fixed bottom-4 right-4 text-white p-3 rounded-full transition-all duration-300 z-50"
          aria-label="Scroll to top"
        >
          <ChevronUp size={24} className="text-white" />
        </button>
      )}
      <div className="container mx-auto">
        {/* Main sections */}
        <div className="flex flex-wrap justify-between mb-8 gap-8">
          {/* Solution Section */}
          <div>
